.video-info {
  display: flex;
  margin: 10px 0;
  height: 100%;
  &-left {
    width: 40%;
    padding: 0 10px;
    display: flex;
    flex-direction: column;
    gap: 10px;
  }
  &-right {
    width: 60%;
    padding: 0 10px;
    overflow: scroll;
    -webkit-overflow-scrolling: touch;

    a {
      color: #0066cc;
      text-decoration: none;
      &:hover {
        text-decoration: underline;
      }
    }
  }
  &-title {
    font-size: 1.2rem;
    font-weight: bold;
    color: #0066cc;
    text-decoration: none;
  }
  &-image {
    width: 100%;
  }
  &-no-image {
    max-height: 300px;
    min-height: 200px;
  }
  &-price {
    color: red;
    font-weight: bold;
    font-size: 1.4rem;
  }
}


.purchase-button {
  width: 100%;
  background-color: #ff0000;
  color: white;
  padding: 12px 0;
  border-radius: 8px;
  font-family: 'Roboto', sans-serif;
  text-align: center;
  text-decoration: none;
  display: block;
}

.image-wrapper {
  position: relative;
  width: 100%;
  min-height: 200px;
  background-color: $no-item-bg-color;
  display: flex;
  align-items: center;
}

.zoom-button {
  position: absolute;
  bottom: 10px;
  right: 10px;
  background: none;
  border: none;
  color: white;
  cursor: pointer;
  width: 40px;
  height: 40px;
  display: flex;
  align-items: center;
  justify-content: center;
  background-color: rgba(0, 0, 0, 0.5);
  border-radius: 50%;
  transition: background-color 0.2s ease;
}

.zoom-icon {
  width: 24px;
  height: 24px;
  transition: transform 0.2s ease;

  &:hover {
    transform: scale(1.1);
  }
}

