import dayjs from 'dayjs';
import 'dayjs/locale/ja';
import type { Item } from '../types/api-product';

dayjs.locale('ja');

export const formatDate = (dateString: string): string => {
  return dayjs(dateString).format('YYYY年M月D日');
};

export const isVideoFound = (item: Item): boolean => {
  return !!item.sampleMovieURL?.size_720_480;
};

