const videoHeight = computed(() => {
  const clientWidth = document.documentElement.clientWidth
  // ユーザーの画面横幅/アスペクト比(横/縦) or 動画最大の高さ
  return clientWidth < maxVideoWidth ? clientWidth / ASPECT_RATIO_720_480 : maxVideoHeight
})

